import { useState, useCallback } from "react"
import { Card, Form, Button, TextField, FormLayout, Heading, TextContainer, Layout } from "@shopify/polaris"
import AddEmail from "./AddEmail";
const MailingList = () => {
    const [url, setUrl] = useState('');
    const handleSubmit = () =>{
        setEmails([...emails,{
            id: Math.random(),
            address: url
        }]
        )
    }
    const handleUrlChange = useCallback((value) => setUrl(value), []);

    console.log(url)

    const [emails,setEmails] = useState([
        {
            id: 1,
            address: "contact@emailmywebhooks.com"
        },
    ])


    return (
        <Layout>
            <Layout.Section oneHalf>
                <Card.Section>
                    <Card.Section>
                        <TextContainer>
                            <Heading>Mailing list</Heading>
                            <p>
                                Add email addresses to the mailing list that will receive notifications. Whenever a notification is triggered, an email will be sent to all the addresses on this list.
                            </p>
                        </TextContainer>
                    </Card.Section>
                </Card.Section>
            </Layout.Section>
            <Layout.Section oneHalf>
                <Card.Section></Card.Section>
                <Card>
                    <Card.Section>
                        <Form noValidate onSubmit={handleSubmit}>
                            <FormLayout>
                                <TextField
                                    value={url}
                                    onChange={handleUrlChange}
                                    placeholder="Add an email address"
                                    type="url"
                                    autoComplete="off"
                                    connectedRight={<Button>Add</Button>}
                                />
                            </FormLayout>
                        </Form>
                    </Card.Section>
                    <Card.Section>
                        {emails.map((email) => (
                            <AddEmail key={email.id} address={email.address} />
                        )
                        )}
                    </Card.Section>
                </Card>
                <Card.Section></Card.Section>
            </Layout.Section>
        </Layout>
    )

}

export default MailingList
