import React,{useState, useCallback} from "react";
import { Page, Card, List, Button, Scrollable, Sheet, Heading, ChoiceList } from "@shopify/polaris";
const EmailPreview =()=>{
    const [sheetActive, setSheetActive] = useState(false);
    const toggleSheetActive = useCallback(
      () => setSheetActive((sheetActive) => !sheetActive),
      [],
    );
    return (
      <Page>
        <Card>
        <Button onClick={toggleSheetActive}>Manage sales channels</Button>
        </Card>
        <Sheet
          open={sheetActive}
          onClose={toggleSheetActive}
        >
          <div
            style={{
              display: 'flex',
              flexDirection: 'column',
              height: '100%',
            }}
          >
            <div
              style={{
                alignItems: 'center',
                borderBottom: '1px solid #DFE3E8',
                display: 'flex',
                justifyContent: 'space-between',
                padding: '1rem',
                width: '100%',
              }}
            >
              <Heading>Manage sales channels</Heading>
              <Button
                accessibilityLabel="Cancel"
                onClick={toggleSheetActive}
                plain
              />
            </div>
            <div
              style={{
                alignItems: 'center',
                borderTop: '1px solid #DFE3E8',
                display: 'flex',
                justifyContent: 'space-between',
                padding: '1rem',
                width: '100%',
              }}
            >
              <Button onClick={toggleSheetActive}>Cancel</Button>
              <Button primary onClick={toggleSheetActive}>
                Done
              </Button>
            </div>
          </div>
        </Sheet>
      </Page>
    );
  }

  export default EmailPreview
