import React, { useState } from "react";
import { CircleTickOutlineMinor } from '@shopify/polaris-icons';
import { Stack, TextContainer, Button, Icon, Card } from "@shopify/polaris";
const NotificationItem = ({ content }) => {
    const [iconValue, setIconValue] = useState("base")
    const [buttonState, setButtonState] = useState("Add")
    const [buttonColor, setButtonColor] = useState("")

    const subscribe = () => {
        setIconValue("success")
        setButtonState("Remove")
    }

    return (
        <Card.Section>
            <Stack>
                <Stack.Item>
                    <Icon
                        source={CircleTickOutlineMinor}
                        color={iconValue} />
                </Stack.Item>
                <Stack.Item fill>
                    <TextContainer>{content}</TextContainer>
                </Stack.Item>
                <Stack.Item>
                    <Button plain>Preview</Button>
                </Stack.Item>
                <Stack.Item>
                    <Button onClick={subscribe} plain destructive="true">{buttonState}</Button>
                </Stack.Item>
            </Stack>
        </Card.Section>
    )
}

export default NotificationItem
