import React, { useState } from "react";
import { CircleTickOutlineMinor } from '@shopify/polaris-icons';
import { Stack, TextContainer, Button, Icon, Card } from "@shopify/polaris";
const NotificationItem = ({ content }) => {
    const [iconValue, setIconValue] = useState("base")
    const [buttonState, setButtonState] = useState("Add")
    const [buttonColor, setButtonColor] = useState(false)

    const subscribe = () => {
        if(iconValue === "base"){
        setButtonColor(true)
        setButtonState("Delete")
        setIconValue("high")
        }else{
            setButtonColor(false)
            setButtonState("Add")
            setIconValue("base")
        }
    }

    return (
        <Card.Section>
            <Stack>
                <Stack.Item>
                    <Icon
                        source={CircleTickOutlineMinor}
                        color={iconValue} />
                </Stack.Item>
                <Stack.Item fill>
                    <TextContainer>{content}</TextContainer>
                </Stack.Item>
                <Stack.Item>
                <Sheet
          open={sheetActive}
          onClose={toggleSheetActive}
        >
          <div
            style={{
              display: 'flex',
              flexDirection: 'column',
              height: '100%',
            }}
          >
            <div
              style={{
                alignItems: 'center',
                borderBottom: '1px solid #DFE3E8',
                display: 'flex',
                justifyContent: 'space-between',
                padding: '1rem',
                width: '100%',
              }}
            >
              <Heading>Manage sales channels</Heading>
              <Button
                accessibilityLabel="Cancel"
                onClick={toggleSheetActive}
                plain
              />
            </div>
            <div
              style={{
                alignItems: 'center',
                borderTop: '1px solid #DFE3E8',
                display: 'flex',
                justifyContent: 'space-between',
                padding: '1rem',
                width: '100%',
              }}
            >
              <Button onClick={toggleSheetActive}>Cancel</Button>
              <Button primary onClick={toggleSheetActive}>
                Done
              </Button>
            </div>
          </div>
        </Sheet>
                    <Button plain>Preview</Button>
                </Stack.Item>
                <Stack.Item>
                    <Button onClick={subscribe} plain destructive={buttonColor}>{buttonState}</Button>
                </Stack.Item>
            </Stack>
        </Card.Section>
    )
}

export default NotificationItem
