import { useState, useCallback } from "react";
import { Tabs, Card, Heading, TextContainer, Stack } from "@shopify/polaris";
const Navbar = () => {
    const [selected, setSelected] = useState(0);

    const handleTabChange = useCallback(
        (selectedTabIndex) => setSelected(selectedTabIndex),
        [],
    );

    const tabs = [
        {
            id: 'all-customers-fitted-2',
            content: 'ACCOUNT',
            accessibilityLabel: 'All customers',
            panelID: 'all-customers-fitted-content-2',
        },
        {
            id: 'accepts-marketing-fitted-2',
            content: 'CONFIGURE',
            panelID: 'accepts-marketing-fitted-Ccontent-2',
        },
    ];

    return (
        <Card>
            <Stack>


            <Card.Section>
                <Tabs tabs={tabs} selected={selected} onSelect={handleTabChange} fitted>
                </Tabs>
            </Card.Section>
            <Card.Section sectioned>
                <TextContainer>
                    <Heading>Mailing List</Heading>
                    <p>
                        Add email addresses to the mailing list that will receive notifications. Whenever a notification is triggered, an email will be sent to all the addresses on this list.
                    </p>
                </TextContainer>
            </Card.Section>
            </Stack>
        </Card>
    );
}

export default Navbar
