import React, { useState, useCallback } from "react";
import { CircleTickOutlineMinor } from '@shopify/polaris-icons';
import { Stack, TextContainer, Button, Icon, Card } from "@shopify/polaris";
const NotificationItem = ({ content }) => {
    const [iconValue, setIconValue] = useState("base")
    const [buttonState, setButtonState] = useState("Add")
    const [buttonColor, setButtonColor] = useState(false)
    const [sheetActive, setSheetActive] = useState(true);
    const toggleSheetActive = useCallback(
        () => setSheetActive((sheetActive) => !sheetActive),
        [],
    );
    const [selected, setSelected] = useState([]);
    const handleSelectedChange = useCallback((value) => setSelected(value), []);

    const subscribe = () => {
        if (iconValue === "base") {
            setButtonColor(true)
            setButtonState("Delete")
            setIconValue("highlight")
        } else {
            setButtonColor(false)
            setButtonState("Add")
            setIconValue("base")
        }
    }

    return (
        <Card.Section>
            <Stack>
                <Stack.Item>
                    <Icon
                        source={CircleTickOutlineMinor}
                        color={iconValue} />
                </Stack.Item>
                <Stack.Item fill>
                    <TextContainer>{content}</TextContainer>
                </Stack.Item>
                <Stack.Item>
                    <Sheet
                        open={sheetActive}
                        onClose={toggleSheetActive}
                        accessibilityLabel="Manage sales channels"
                    >
                                <Button plain>Preview</Button>  <Button onClick={toggleSheetActive}>Cancel</Button>
                                <Button primary onClick={toggleSheetActive}>
                                    Done
                                </Button>
                            </Sheet>
                        </Stack.Item>
                        <Stack.Item>
                            <Button onClick={subscribe} plain destructive={buttonColor}>{buttonState}</Button>
                        </Stack.Item>
                    </Stack>
                </Card.Section>
                )
}

                export default NotificationItem
